mapper.insert:
insert into all_user (
  user_name, user_password, login_time, aid, star, is_vip, eid
) VALUES (
 #{user_name}, #{user_password}, #{login_time}, #{aid}, #{star}, #{is_vip}, #{eid}
)

mapper.update:
update all_user
set star = #{star}
where uid = #{uid}

update all_user
set eid = #{eid}
where uid = #{uid}

update all_user
set aid = #{aid}
where uid = #{uid}

update all_user
set user_password = #{password}
where uid = #{uid}

