mapper.insert:
        insert into time_trial_user_map ( cid, uid )
				   values
				   (#{cid}, #{uid});

        insert into time_trial_record ( uid, cid, complete_time, record_time)
				   values
				   (#{uid}, #{cid}, #{complete_time}, #{record_time});

mapper.select:
select rank_num 
from time_trial_rank_${uid}_${cid} 
where uid = #{uid};

mapper.update:
update time_trial_class
set map_id = #{map_id}, has_water = #{has_water}, star = #{star}, 
 limit_time = #{limit_time}, class_name = #{class_name}, times = #{times}
where cid = #{cid}
